<!DOCTYPE html>
<html lang="en">

<head>
      <link rel="shortcut icon" href="data:image/png;base64,">
  
  <meta charset="utf-8">
  <meta http-equiv="x-ua-compatible" content="ie=edge">
  <title>Usuarios</title>
  <meta name="description" content="">
  <meta name="viewport" content="width=device-width, initial-scale=1">
  <!-- load frameworks: vue + bulma + fontawesome -->
  <script src="https://unpkg.com/vue@3/dist/vue.global.prod.js"></script>
  <script src="https://unpkg.com/vue-router@4.0.15/dist/vue-router.global.prod.js"></script>
  <link rel="stylesheet" href="https://cdn.jsdelivr.net/npm/bulma@0.9.4/css/bulma.min.css">
  <link rel="stylesheet" href="https://unpkg.com/@fortawesome/fontawesome-free@6.2.0/css/all.min.css">
</head>

<body>

  <section class="section">
    <div class="container">
      <nav class="navbar" role="navigation" aria-label="main navigation">
        <div class="navbar-brand">
          <a class="navbar-item" href="#/">
        <img src="data:image/png;base64,"> </a>
          <a class="navbar-item is-size-2" href="#/">
            Usuarios registrados
          </a>
        </div>
      </nav>
      <main id="app" class="mt-4">
        <router-view></router-view>
      </main>
      
      <footer class="footer mt-4">
        <div class="content">
        <br>
           <a class="home_bottom" style="  background-color:#27ae60;padding-left:10px;padding-right:10px;padding-top:10px;padding-bottom:10px;
 border-top-left-radius:10px;border-top-right-radius:10px;border-bottom-left-radius:10px;border-bottom-right-radius:10px;box-shadow:none;white-space:normal;letter-spacing:2.9px;text-align:center; color:white; text-decoration: none; "
                href="carga_home">Volver</a>
        
          <p style="color:aqua;">Camilo Abel Sol Mosqurea <br> Hugo Javier Ledesma <b>@Derechos reservados Â¡mi papa!</b></p>
       
        </div>
      </footer>
    </div>
  </section>

  <template id="product-list">
    <div>
      <h1 class="title">Usuarios</h1>
      <router-link class="button" to="/add-product">
        <span class="icon">
          <i class="fa-solid fa-plus"></i>
        </span>
        <span>Agregar usuario</span>
      </router-link>
      <div class="columns mt-2">
        <div class="column is-one-fifth">
          <div class="field">
            <p class=" control has-icons-right">
              <input class="input" v-model="searchKey" type="text" placeholder="Buscar">
              <span class="icon is-small is-right">
                <i class="fa-solid fa-magnifying-glass"></i>
              </span>
            </p>
          </div>
        </div>
      </div>
      <br><br>
      <div class="tableshon">
      <table class="table is-striped">
        <thead>
          <tr >
            <th>Nombre</th>
            <th>Correo Electronico</th>
            <th>Semestre</th>
            <th class="col-sm-2">Acciones</th>
          </tr>
        </thead>
        <tbody>
          <tr v-for="product in filteredProducts">
            <td>
              <router-link v-bind:to="{name: 'product', params: {product_id: product.id}}">{{
                product.name
                }}</router-link>
            </td>
            <td>{{ product.description }}</td>
            <td>
              {{ product.price }}
          
            </td>
            <td class="py-1">
              <div class="buttons">
                <router-link class="button is-warning is-small" v-bind:to="{name: 'product-edit', params: {product_id: product.id}}">Editar
                </router-link>
                <router-link class="button is-danger is-small" v-bind:to="{name: 'product-delete', params: {product_id: product.id}}">
                  Eliminar
                </router-link>
              </div>
            </td>
          </tr>
        </tbody>
      </table>
      </div>
    </div>
  </template>

  <template id="add-product">
    <div>
      <h1 class="title">Agregar nuevo usuario</h1>
      <form v-on:submit="createProduct">
        <div class="field">
          <label class="label" for="add-name">Nombre</label>
          <input class="input" id="add-name" v-model="product.name" required />
        </div>
        <div class="field">
          <label class="label" for="add-description">Correo electronico</label>
          <input class="input" id="add-description" v-model="product.description">
        </div>
        <div class="field">
          <label class="label" for="add-price">Semestre</label>
          <input type="number" class="input" id="add-price" v-model="product.price" />
        </div>
        <div class="buttons">
          <button type="submit" class="button is-primary">Crear</button>
          <router-link class="button" to="/">Cancelar</router-link>
        </div>
      </form>
    </div>
  </template>

  <template id="product">
    <div>
      <h1 class="title">{{ product.name }}</h1>
      <b>Correo electronico: </b>
      <div>{{ product.description }}</div>
      <b>Price:</b>
      <div>{{ product.price }}</div>
      <br />
      <router-link to="/"><i class="fa-solid fa-arrow-left"></i> Ver toda la lista</router-link>
    </div>
  </template>

  <template id="product-edit">
    <div>
      <h1 class="title">Editar usuario</h1>
      <form v-on:submit="updateProduct">
        <div class="field">
          <label class="label" for="edit-name">Nombre</label>
          <input class="input" id="edit-name" v-model="product.name" required />
        </div>
        <div class="field">
          <label class="label" for="edit-description">Correo electronico</label>
          <input class="input" id="edit-description" v-model="product.description">
        </div>
        <div class="field">
          <label class="label" for="edit-price">Semestre</label>
          <input type="number" class="input" id="edit-price" v-model="product.price" />
        </div>
        <div class="buttons">
          <button type="submit" class="button is-primary">Guardar</button>
          <router-link class="button" to="/">Cancelar</router-link>
        </div>
      </form>
    </div>
  </template>

  <template id="product-delete">
    <div>
      <h1 class="title">Delete product "{{ product.name }}"</h1>
      <form v-on:submit="deleteProduct">
        <div class="notification is-warning">
          The action cannot be undone.
        </div>
        <div class="buttons">
          <button type="submit" class="button is-danger">Eliminar</button>
          <router-link class="button" to="/">Cancelar</router-link>
        </div>
      </form>
    </div>
  </template>

</body>

</html>

