<script>
  
var products = [
  {
    id: 1,
    name: "Hugo",
    description: "VIVAELPAM@GMAIL.COM",
    price: 4
  },
  {
    id: 2,
    name: "Camilo",
    description: "Superman@hotmail.com",
    price: 7
  },
  {
    id: 3,
    name: "Ledezma",
    description: "bebta@outlook.es",
    price: 5
  }
];

function findProduct(productId) {
  return products[findProductKey(productId)];
}

function findProductKey(productId) {
  for (var key = 0; key < products.length; key++) {
    if (products[key].id == productId) {
      return key;
    }
  }
}

var List = {
  template: "#product-list",
  data: function () {
    return { products: products, searchKey: "" };
  },
  computed: {
    filteredProducts: function () {
      return this.products.filter(function (product) {
        return (
          this.searchKey == "" ||
          product.name.toLowerCase().indexOf(this.searchKey.toLowerCase()) !==
            -1 ||
          product.description
            .toLowerCase()
            .indexOf(this.searchKey.toLowerCase()) !== -1
        );
      }, this);
    }
  }
};

var Product = {
  template: "#product",
  data: function () {
    return { product: findProduct(this.$route.params.product_id) };
  }
};

var ProductEdit = {
  template: "#product-edit",
  data: function () {
    return { product: findProduct(this.$route.params.product_id) };
  },
  methods: {
    updateProduct: function () {
      var product = this.product;
      products[findProductKey(product.id)] = {
        id: product.id,
        name: product.name,
        description: product.description,
        price: product.price
      };
      router.push("/");
    }
  }
};

var ProductDelete = {
  template: "#product-delete",
  data: function () {
    return { product: findProduct(this.$route.params.product_id) };
  },
  methods: {
    deleteProduct: function () {
      products.splice(findProductKey(this.$route.params.product_id), 1);
      router.push("/");
    }
  }
};

var AddProduct = {
  template: "#add-product",
  data: function () {
    return { product: { name: "", description: "", price: "" } };
  },
  methods: {
    createProduct: function () {
      var product = this.product;
      products.push({
        id: Math.random().toString().split(".")[1],
        name: product.name,
        description: product.description,
        price: product.price
      });
      router.push("/");
    }
  }
};

var router = new VueRouter.createRouter({
  history: VueRouter.createWebHashHistory(),
  routes: [
    { path: "/", component: List },
    { path: "/product/:product_id", component: Product, name: "product" },
    { path: "/add-product", component: AddProduct },
    {
      path: "/product/:product_id/edit",
      component: ProductEdit,
      name: "product-edit"
    },
    {
      path: "/product/:product_id/delete",
      component: ProductDelete,
      name: "product-delete"
    }
  ]
});

const app = Vue.createApp({});
app.use(router);
app.mount("#app");

</script>